t = tab.new()
assert.eq(t.url, "about:blank")
t:go(TEST.url()):wait("body")
assert.eq(t("body").text, "hello world!")
assert.eq(t.title, "world - test")
t:close()

t = tab.new(TEST.url("/?target=webscenario")):wait("body")
assert.eq(t("body").text, "hello webscenario!")
assert.eq(t.title, "webscenario - test")
t:close()

t = tab.new({ width=1280, height=720 })
assert.eq(t.url, "about:blank")
assert.eq(t.viewport, { width=1280, height=720 })
t:close()

t = tab.new({ width=640, height=480, url=TEST.url("/?target=viewport") })
assert.eq(t.url, TEST.url("/?target=viewport"))
assert.eq(t.viewport, { width=640, height=480 })
t:close()
